mapper.select:
select postIdx, categoryIdx, userIdx, title, content, hit, createTime
from board
where title like '%${title}%';

select postIdx, categoryIdx, userIdx, title, content, hit, createTime
from board
where content like '%${content}%';

select postIdx, categoryIdx, userIdx, title, content, hit, createTime
from board
where postIdx = #{boardIdx};

select postIdx, categoryIdx, userIdx, title, content, hit, createTime
from board
where categoryIdx = #{categoryIdx};
